const [showFullDescription, setShowFullDescription] = useState(false);
const handleToggleDescription = () => {
    setShowFullDescription(!showFullDescription);};


    return (
        <>
            <Card style={{ width: '18rem' }}>
                <Card.Img variant="top" src={props.tour.image} />
                <Card.Body>
                    <Card.Title>{props.tour.name}</Card.Title>

                    <Link to={`/city/:${props.tour.id}`}>View Details</Link>

            <div>
              <div className="description">
               {showFullDescription ? description : `${description.slice(0, 3)}...`}
              </div>
             {!showFullDescription && (
             <button onClick={handleToggleDescription}>See More</button>)}
            </div>

                </Card.Body>
            </Card>   

        </>
    )
}

export default Tour;
