import React from 'react';
import { useParams } from 'react-router-dom';
import { useState } from 'react';


function TourDetails()
{
    const { id } = useParams(); 

    return (
      <div>

        <h1> Tour Details</h1>

       </div>
    );
  ;

}


export default TourDetails;


const MyComponent = ({ description }) => {
    const [showFullDescription, setShowFullDescription] = useState(false);

    return (
        <div>
          {description.slice(0, 3)} {/* Render the first three lines of the description */}
          {!showFullDescription && (
            <button onClick={() => setShowFullDescription(true)}>See More</button>
          )}
          {showFullDescription && (
            <div>
              {description} {/* Render the full description */}
              <button onClick={() => setShowFullDescription(false)}>See Less</button>
            </div>
          )}
        </div>
      );}
