import React from 'react';
import Header from '../header/Header';
import Footer from '../footer/Footer';
import { useParams } from 'react-router-dom';
import './TourDetails.css'
const data = require('./../../data/db.json');
import { useState } from "react";

const TourDetails = (props) => {

  const [expanded, setExpanded] = useState(false);
  const toggleDescription = () => {
      setExpanded(!expanded);
    };


  const { id } = useParams();
  console.log(data);
  // const result = data.filter(t => t.id === id);
  const result = data.filter(t => t.id === id);

  console.log(result);


  return (
    <>
      <Header />
      <div className='description'>
        <h2> {result[0].name}</h2>
        <img src={data.find(t => t.id === id).image} alt={data.find(t => t.id === id).id} />

        <p>City ID: {id}</p>
        <p> Description of tour: {result[0].info}</p>
        <p> Price of tour: {result[0].price}$</p>

        {expanded ? (
              <div>
                <p>{result}</p>
                <button onClick={toggleDescription}>See Less</button>
              </div>
            ) : (
              <div>
                <p>{result}</p>
                <button onClick={toggleDescription}>See More</button>
              </div>
            )}



      </div>
      <Footer />
    </>

  );


};
export default TourDetails;
